import React, { useContext } from "react";
import "./Contact.css";
import { themeContext } from "../../Context";
import { images } from "../../constants";
import FloatinDiv from "../FloatingDiv/FloatingDiv";
import { motion } from "framer-motion";
import { useTranslation } from 'react-i18next';

const Contact = () => {


  const theme = useContext(themeContext);
  const darkMode = theme.state.darkMode;
  const scaleVariants = {
    whileInView: {
      scale: [0.5, 1],
      opacity: [0, 1],
      transition: {
        duration: 1,
        ease: "easeInOut",
      },
    },
  };

  return (
    <div className="contact-form" id="contact">
      {/* left side copy and paste from work section */}
      <div className="w-left">
        <div className="awesome">
          {/* darkMode */}
          <span style={{ color: darkMode ? "white" : "" }}>
            Contact <span className="text-2"> Me</span>
          </span>
          <div
            className="blur s-blur1"
            style={{ background: "#ABF1FF94" }}
          ></div>
        </div>
      </div>
      {/* right side form */}

      <motion.div
        variants={scaleVariants}
        whileInView={scaleVariants.whileInView}
        className="c-right"
      >
        <motion.div
          whileHover={{ scale: 1.4 }}
          transition={{ duration: 0.8, ease: "easeInOut" }}
          className="floating-div"
        >
          <FloatinDiv img={images.email} text1="sorawit.sa@rmuti.ac.th" />
        </motion.div>

        <motion.div
          whileHover={{ scale: 1.4 }}
          transition={{ duration: 0.8, ease: "easeInOut" }}
          className="floating-div"
        >
          <FloatinDiv img={images.facebook} text1="Sorawit's Nukker" />
        </motion.div>

        <motion.div
          whileHover={{ scale: 1.4 }}
          transition={{ duration: 0.8, ease: "easeInOut" }}
          className="floating-div"
        >
          <FloatinDiv img={images.instagram} text1="nukker_srw" />
        </motion.div>

        <motion.div
          whileHover={{ scale: 1.4 }}
          transition={{ duration: 0.8, ease: "easeInOut" }}
          className="floating-div"
        >
          {/* <a href="https://www.instagram.com/nukker_srw/" target="something">
            <FloatinDiv img={images.github} text1="Nuksaker" />
          </a>    */}
          <FloatinDiv img={images.github} text1="Nuksaker" />
        </motion.div>
      </motion.div>
      <div
        className="blur c-blur1"
        style={{
          background: "#0affde",
          top: "17rem",
          width: "21rem",
          height: "11rem",
          left: "-9rem",
        }}
      ></div>

      <div className="App">
        <section className="hero is-info">
          <div className="hero-body has-text-centered">
            <div className="container">
              <h1 className="title">{t("title")}</h1>
              <h2>{t("subtitle")}</h2>
            </div>
          </div>
        </section>

        <div className="language-button-section">
          <span>{t("lang_text")}</span>
          <button
            className="button"
            onClick={() => {
              i18n.changeLanguage("th");
            }}
          >
            {t("lang_th")}
          </button>
          <button
            className="button"
            onClick={() => {
              i18n.changeLanguage("en");
            }}
          >
            {t("lang_en")}
          </button>
        </div>

        <div className="container columns">
          <div className="column is-8 is-offset-4">
            <div className="box normal-content">
              <h3 className="title">{t("header")}</h3>
              <p>{t("content")}</p>
            </div>

            <div className="box interpolate-content">
              <p>
                {t("welcome_message", {
                  name: "Chai Phonbopit",
                  website: "devahoy.com",
                })}
              </p>
            </div>

            <div className="box info">
              <h3 className="title">{t("read_more")}</h3>
              <ul>
                <li>
                  <strong>{t("post")}</strong>{" "}
                  <a
                    href="https://devahoy.com/posts/getting-started-with-react-18next/"
                    target="_blank"
                  >
                    วิธีการทำ React 2 ภาษาด้วย React i18n-next
                  </a>
                </li>
                <li>
                  <strong>{t("source_code")}</strong>{" "}
                  <a
                    href="https://github.com/Devahoy/hello-react-i18next"
                    target="_blank"
                  >
                    hello-react-i18next
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </div>
        <footer className="footer">
          <div className="container">
            <div className="content has-text-centered">
              <p>
                Powered by{" "}
                <a href="https://devahoy.com" target="_blank">
                  devahoy.com
                </a>
              </p>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
};

export default Contact;
